import React, { Component } from "react";
import { Slider, SafeAreaView, Picker, Text, StyleSheet, View, TextInput, Image, Button, ScrollView, ImageBackground } from "react-native";

import { TouchableOpacity } from "react-native-gesture-handler";
const image = { uri: "https://reactjs.org/logo-og.png" };

class category extends React.Component {


  constructor(props) {
    super(props)

    this.state = {

      categorys:this.props.navigation.state.params.data.categorys,
      Type :"New Installation",

    };

  }
  chooseCategory(Type,navigation){
   
    this.setState({ Type: Type });
    navigation.navigate('Order',{data:this.state});
 }

  render() {
    console.log(this.props.navigation.state.params.data)
        console.warn(this.props.navigation.state.params.data );

    const { navigation } = this.props.navigation;

    return (

      <ScrollView style={{ height: "100%",backgroundColor:"#ae1fcfa" }} >

  
<View style={styles.ViewClomButtons,{ height: "100%",backgroundColor:"#ae1fcfa",marginTop: "14%", }}>
        <View style={styles.ViewRowButtons}>
          <View style={[styles.Button1, { borderBottomEndRadius: 0, borderTopLeftRadius: 0 ,}]} >

            <TouchableOpacity   onPress={() => this.chooseCategory("New Installation",this.props.navigation)}>


              <Text style={styles.TextButton}>New Installation</Text>
            </TouchableOpacity>
          </View>


        </View>

        <View style={styles.ViewRowButtons}>
          <View style={[styles.Button1, { borderTopRightRadius: 0, borderBottomLeftRadius: 0, }]} >
            <TouchableOpacity  onPress={() => this.chooseCategory("Maintenance",this.props.navigation)} >
              <Text style={styles.TextButton}> Maintenance</Text>
            </TouchableOpacity>
          </View>
  
        </View>
        <View style={styles.ViewRowButtons}>
          <View style={[styles.Button1, { borderTopRightRadius: 0, borderBottomLeftRadius: 0, }]} >
            <TouchableOpacity  onPress={() => this.chooseCategory("Inquiry",this.props.navigation)} >
              <Text style={styles.TextButton}> Inquiry</Text>
            </TouchableOpacity>
          </View>
  
        </View>
      </View>





      </ScrollView>
    );



  }
}


const styles = StyleSheet.create({

    ViewClomButtons: {
        marginTop: "24%",
      },
      ViewRowButtons: {
    
        marginTop: "6%",
    
        flexDirection: 'row',
    
        justifyContent:"center",
    
        marginLeft: "3%",
        marginRight:"3%",
      },
      Button: {
        borderWidth: 3,
       
        borderRadius: 100,
        backgroundColor: "#000",
        justifyContent:"center",
    
      
      },
      Button1: {
    
        borderWidth: 3,
        borderColor: "#000",
        backgroundColor: "#000",
        marginLeft: "2%",
    
        width:"20%",
        flex: 1,
    
       
        justifyContent:"center",
    
      },
      TextButton: {
    
        fontSize: 20,
        color: "#fff",
        justifyContent:"center",
        marginLeft: "35%",
        marginBottom:"10%",
        marginTop:"10%",
      }
});
export default category;
