import React, { Component } from "react";
import { Text, StyleSheet, View, TextInput, Image, Button, ScrollView, ImageBackground } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import { SliderBox } from "react-native-image-slider-box";


class HomeScreen extends React.Component {



  constructor(props) {
    super(props)

    this.state = {
      images: [

        require('./../../assets/LogoSlider.png'),    
        "https://source.unsplash.com/1024x768/?nature",
        "https://source.unsplash.com/1024x768/?water",
        "https://source.unsplash.com/1024x768/?girl",
        "https://source.unsplash.com/1024x768/?tree", // Network image
        "https://source.unsplash.com/1024x768/?tree", 
      ],
      categorys:"CCTV",


    };
  }


  chooseCategory(category,navigation){


     this.setState({ categorys: category });
     navigation.navigate('category',{data:this.state});
  }

  render() {
    const { navigation } = this.props.navigation;
    return (<ScrollView style={{}}>
      <SliderBox

        images={this.state.images}
        sliderBoxHeight={250}
        onCurrentImagePressed={index => console.warn(`image ${index} pressed`)}
        dotColor="#FFEE58"
        inactiveDotColor="#90A4AE"
        paginationBoxVerticalPadding={20}
        autoplay
        circleLoop
        resizeMethod={'resize'}
        resizeMode={'cover'}
        paginationBoxStyle={{
          position: "absolute",
          bottom: 0,
          padding: 0,
          alignItems: "center",
          alignSelf: "center",
          justifyContent: "center",
          paddingVertical: 10
        }}
        dotStyle={{
          width: 10,
          height: 10,
          borderRadius: 5,
          marginHorizontal: 0,
          padding: 0,
          margin: 0,
          backgroundColor: "rgba(128, 128, 128, 0.92)"
        }}
        ImageComponentStyle={{ borderRadius: 15, width: '97%', marginTop: 5 }}
        imageLoadingColor="#2196F3"
      />


      <View style={styles.ViewClomButtons}>
        <View style={styles.ViewRowButtons}>
          <View style={[styles.Button1, { borderBottomEndRadius: 0, borderTopLeftRadius: 0 ,}]} >

            <TouchableOpacity   onPress={() => this.chooseCategory("CCTV",this.props.navigation)}>


              <Text style={styles.TextButton}> CCTV</Text>
            </TouchableOpacity>
          </View>
          <View style={[styles.Button1, { borderBottomLeftRadius: 0, borderTopRightRadius: 0, }]}>

            <TouchableOpacity  onPress={() => this.chooseCategory("Satclite",this.props.navigation)} >

              <Text style={styles.TextButton}> Satclite</Text>
            </TouchableOpacity>
          </View>

        </View>

        <View style={styles.ViewRowButtons}>
          <View style={[styles.Button1, { borderTopRightRadius: 0, borderBottomLeftRadius: 0, }]} >
            <TouchableOpacity  onPress={() => this.chooseCategory("Network",this.props.navigation)} >
              <Text style={styles.TextButton}> Network</Text>
            </TouchableOpacity>
          </View>
          <View style={[styles.Button1, { borderTopLeftRadius: 0, borderBottomEndRadius: 0 }]}>

            <TouchableOpacity   onPress={() => this.chooseCategory("Arial",this.props.navigation)}>


              <Text style={styles.TextButton}> Arial</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

    </ScrollView>
    );

  }

}
const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  ViewClomButtons: {
    marginTop: "14%",
  },
  ViewRowButtons: {

    marginTop: "2%",

    flexDirection: 'row',

    justifyContent:"center",

    marginLeft: "3%",
    marginRight:"3%",
  },
  Button: {
    borderWidth: 3,

    borderRadius: 100,
    backgroundColor: "#3a0052",


    justifyContent:"center",


  },
  Button1: {

    borderWidth: 3,
    borderRadius: 30,
    borderColor: "#000",
    backgroundColor: "#000",
    marginLeft: "2%",

    width:"40%",
    flex: 1,


    justifyContent:"center",

  },
  TextButton: {

    fontSize: 20,
    color: "#fff",
    justifyContent:"center",
    marginLeft: "33%",
    marginBottom:"40%",
    marginTop:"40%",
  }
});


export default HomeScreen;
