import React, { Component } from "react";
import { Dimensions,Slider, SafeAreaView, Picker, Text, StyleSheet, View, TextInput, Image, Button, ScrollView, ImageBackground } from "react-native";

import { TouchableOpacity } from "react-native-gesture-handler";
const image = { uri: "https://reactjs.org/logo-og.png" };

class Order extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            Order:"",
            categorys:this.props.navigation.state.params.data.categorys,
            Type :this.props.navigation.state.params.data.Type,

          };
    }

    chooseCategory(navigation){


        navigation.navigate('Map',{data:this.state});
     }
   

    render() {
        console.log(this.props.navigation.state.params.data);
 
        const width = 20;
        return (

            <ScrollView style={{ height: "100%", backgroundColor: "#ae1fcfa" }} >
              <View  >
                                  <View style={styles.center}>
                    <Text style={styles.textTitle}>Order details </Text>
                    <View style={styles.textAreaContainer} >
                        <TextInput
                            style={styles.textArea}
                            underlineColorAndroid="transparent"
                            placeholder=" Order ...f"
                            placeholderTextColor="grey"
                            numberOfLines={20}
                            multiline={true}
                            onChangeText={text => this.setState({ Order: text })}
                        />
                    </View>


                </View>
                <View style={[styles.Button1,]}>

                    <TouchableOpacity onPress={() => this.chooseCategory( this.props.navigation)}>


                        <Text style={styles.TextButton}> Submit </Text>
                    </TouchableOpacity>
                </View>

                </View>
            </ScrollView>
        );



    }
}

const widthWindow = Dimensions.get('window').width;
const heightWindow = Dimensions.get('window').height;
const styles = StyleSheet.create({

    center: {

        justifyContent: "center",
        flex: 1,
        marginRight: "4%",
        marginLeft: "2%",

    },
    textTitle: {
        marginLeft: "25%",
        fontSize: 34,
        marginBottom: "5%",

    },
    textAreaContainer: {
        borderWidth: 3,
        borderColor: "#000",

         
    },
    textArea: {
        marginLeft:"2%",
        paddingBottom:"60%",
        height: heightWindow * "0.60",
        justifyContent: "flex-start"
    },



    Button1: {
        marginTop:"1%",  
     
        borderWidth: 3,
        borderColor: "#000",
        backgroundColor: "#000",
        marginLeft: "2%",
        marginRight: "6%",
         width: widthWindow * "0.94",

        flex: 1,


        justifyContent: "center",

    },
    TextButton: {

        fontSize: 20,
        color: "#fff",
        justifyContent: "center",
        marginLeft: "40%",
        marginBottom: "10%",
        marginTop: "10%",
    }
});
export default Order;
